  // const pricingDataRef = useRef([]);
  // const locationsRef = useRef([]);

  // // Update refs when data changes
  // const updatePricingDataRef = (data) => {
  //   pricingDataRef.current = data;
  // };

  // const updateLocationsRef = (data) => {
  //   locationsRef.current = data;
  // };

  // Function to check if a point is within the drawn shape
  const isPointInShape = (lat, lng, layer) => {
    if (layer instanceof L.Circle || layer instanceof L.CircleMarker) {
      const center = layer.getLatLng();
      const radius = layer.getRadius();
      return map.distance(center, L.latLng(lat, lng)) <= radius;
    }
    const shapeGeoJSON = layer.toGeoJSON();
    const polygonGeometry = polygon(shapeGeoJSON.geometry.coordinates);
    return booleanPointInPolygon(point([lng, lat]), polygonGeometry);
  };

  // Function to handle data filtering when a shape is drawn
  const handleShapeDrawn = (layer) => {
    const pricingData = pricingDataRef.current;
    const locations = locationsRef.current;

    const filteredPricing = pricingData.filter((p) => {
      const [lat, lng] = p.coords;
      return isPointInShape(lat, lng, layer);
    });

    const filteredComps = locations.filter((loc) => {
      const [lat, lng] = loc.coords;
      const l = isPointInShape(lat, lng, layer);
      console.log(l);
      return l;
      // return isPointInShape(lat, lng, layer);
    });

    // console.log({ filteredPricing, filteredComps });
    const combinedData = [
      ...filteredPricing.map((item) => ({
        type: "pricing",
        apn: item.apn,
        lotAcreage: item.lotAcreage,
        latitude: item.coords[0],
        longitude: item.coords[1],
      })),
      ...filteredComps.map((item) => ({
        type: "comps",
        price: item.details.PRICE,
        acres: item.details.ACRES,
        latitude: item.coords[0],
        longitude: item.coords[1],
      })),
    ];

    setShapeFilteredData(combinedData);
    console.log(combinedData);
  };

  // Initialize drawing controls once
  useEffect(() => {
    if (!controlAdded) {
      const drawnItems = new L.FeatureGroup();
      map.addLayer(drawnItems);
      const drawControl = new L.Control.Draw({
        draw: {
          marker: false,
          polyline: false,
          circle: { shapeOptions: { color: "#ff0000" }, showRadius: true },
          circlemarker: { shapeOptions: { color: "#ff0000" } },
          polygon: { shapeOptions: { color: "#0000ff" }, showArea: true },
          rectangle: { shapeOptions: { color: "#00ff00" } },
        },
        edit: { featureGroup: drawnItems },
      });
      map.addControl(drawControl);
      setControlAdded(true);

      map.on("draw:created", (e) => {
        const { layer } = e;
        drawnItems.addLayer(layer);
        handleShapeDrawn(layer);
      });
    }
    // The dependencies are only map and controlAdded
  }, [map, controlAdded]);

  // Expose update functions to parent component via context or other means
  // However, since DrawHandler only needs to setShapeFilteredData, and
  // we're handling refs within this component, we don't need to pass data as props.

  return null;
}

// Main App Component
function App() {
  // Master data lists
  const [locations, setLocations] = useState([]); // From Main CSV
  const [pricingData, setPricingData] = useState([]); // From Pricing CSV

  // Refs to keep the latest data
  const pricingDataRef = useRef(pricingData);
  const locationsRef = useRef(locations);

  // Update refs whenever data changes
  useEffect(() => {
    pricingDataRef.current = pricingData;
  }, [pricingData]);

  useEffect(() => {
    locationsRef.current = locations;
  }, [locations]);

  // Filtered data lists
  const [shapeFilteredData, setShapeFilteredData] = useState([]); // Data within drawn shape
  const [acreageFilteredData, setAcreageFilteredData] = useState([]); // Data within acreage range

  // Displayed data after applying all filters
  const [displayedPricingData, setDisplayedPricingData] = useState([]);

  // Acreage filter inputs
  const [minAcreage, setMinAcreage] = useState("");
  const [maxAcreage, setMaxAcreage] = useState("");

  const mapRef = useRef(null);

  // Effect to compute displayedPricingData whenever filters change
  useEffect(() => {
    let filtered = pricingData;

    // Apply shape filter if any
    if (shapeFilteredData.length > 0) {
      const shapeAPNs = new Set(shapeFilteredData.map((item) => item.apn));
      filtered = filtered.filter((item) => shapeAPNs.has(item.apn));
    }

    // Apply acreage filter if set
    if (acreageFilteredData.length > 0) {
      const acreageAPNs = new Set(acreageFilteredData.map((item) => item.apn));
      filtered = filtered.filter((item) => acreageAPNs.has(item.apn));
    }

    setDisplayedPricingData(filtered);
  }, [shapeFilteredData, acreageFilteredData, pricingData]);

  // Handler for uploading Main CSV
  const handleMainCSVUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const parsedData = results.data
          .filter((row) => {
            const lat = parseFloat(row.LATITUDE);
            const lng = parseFloat(row.LONGITUDE);
            return Number.isFinite(lat) && Number.isFinite(lng);
          })
          .map((row) => ({
            coords: [parseFloat(row.LATITUDE), parseFloat(row.LONGITUDE)],
            details: row,
          }));
        setLocations(parsedData);
      },
      error: (err) => console.error("PapaParse Error (Main CSV):", err),
    });
  };

  // Handler for uploading Pricing CSV
  const handlePricingCSVUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      chunkSize: 500,
      chunk: (results) => {
        const chunkData = results.data
          .filter((row) => {
            const lat = parseFloat(row.LATITUDE);
            const lng = parseFloat(row.LONGITUDE);
            return (
              Number.isFinite(lat) &&
              Number.isFinite(lng) &&
              row["APN - FORMATTED"]
            );
          })
          .map((row) => ({
            apn: row["APN - FORMATTED"],
            coords: [parseFloat(row.LATITUDE), parseFloat(row.LONGITUDE)],
            lotAcreage: row["LOT ACREAGE"]
              ? parseFloat(row["LOT ACREAGE"])
              : null,
          }));
        setPricingData((prev) => {
          const updated = [...prev, ...chunkData];
          return updated;
        });
      },
      complete: () => console.log("Pricing CSV parsing complete!"),
      error: (err) => console.error("PapaParse Error (Pricing CSV):", err),
    });
  };

  // Handler for applying acreage filter
  const applyAcreageFilter = () => {
    const minVal = parseFloat(minAcreage);
    const maxVal = parseFloat(maxAcreage);

    if (isNaN(minVal) || isNaN(maxVal)) {
      alert("Please enter valid numeric values for both min and max acreage.");
      return;
    }

    const filtered = pricingData.filter((pd) => {
      if (pd.lotAcreage == null) return false;
      return pd.lotAcreage >= minVal && pd.lotAcreage <= maxVal;
    });

    setAcreageFilteredData(filtered);
  };

  // Handler for clearing all filters
  const clearFilters = () => {
    setShapeFilteredData([]);
    setAcreageFilteredData([]);
    setMinAcreage("");
    setMaxAcreage("");
  };

  // Handler for downloading filtered data and removing them from the map
  const downloadFilteredDataToExcel = () => {
    if (displayedPricingData.length === 0) {
      alert("No data points to download.");
      return;
    }

    // Prepare data for export
    const pricingDataTab = displayedPricingData.map((item) => ({
      APN: item.apn,
      "LOT ACREAGE": item.lotAcreage,
      Latitude: item.coords ? item.coords[0] : item.latitude,
      Longitude: item.coords ? item.coords[1] : item.longitude,
    }));

    // Create and download Excel file
    const workbook = XLSX.utils.book_new();
    const pricingSheet = XLSX.utils.json_to_sheet(pricingDataTab);
    XLSX.utils.book_append_sheet(workbook, pricingSheet, "Pricing Data");
    XLSX.writeFile(workbook, "filtered_data.xlsx");

    // Identify APNs of exported points for removal
    const exportedAPNs = new Set(displayedPricingData.map((item) => item.apn));

    // Remove exported points from locations and pricingData states
    setLocations((prev) =>
      prev.filter((loc) => !exportedAPNs.has(loc.details["APN - FORMATTED"]))
    );
    setPricingData((prev) => prev.filter((pt) => !exportedAPNs.has(pt.apn)));

    // Clear filtered data
    setShapeFilteredData([]);
    setAcreageFilteredData([]);
    setDisplayedPricingData([]);

    alert("Filtered data downloaded and removed from the map.");
  };

  return (
    <div style={{ height: "100vh", width: "100%", position: "relative" }}>
      {/* Control Panel */}
      <div
        style={{
          position: "absolute",
          zIndex: 1000,
          top: 10,
          right: 10,
          background: "#f8f9fa",
          padding: "12px 15px",
          borderRadius: "8px",
          boxShadow: "0 3px 8px rgba(0,0,0,0.15)",
          width: "280px",
          fontFamily: "Arial, sans-serif",
        }}
      >
        <h3 style={{ marginTop: 0, marginBottom: "10px" }}>Data Controls</h3>

        {/* Upload Main CSV */}
        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", fontWeight: "bold" }}>
            Upload Main CSV:
          </label>
          <input
            type="file"
            accept=".csv"
            onChange={handleMainCSVUpload}
            style={{ width: "100%", marginTop: "5px" }}
          />
        </div>

        {/* Upload Pricing CSV */}
        <div style={{ marginBottom: "15px" }}>
          <label style={{ display: "block", fontWeight: "bold" }}>
            Upload Pricing CSV:
          </label>
          <input
            type="file"
            accept=".csv"
            onChange={handlePricingCSVUpload}
            style={{ width: "100%", marginTop: "5px" }}
          />
        </div>

        {/* Acreage Filter */}
        <div
          style={{
            marginBottom: "15px",
            border: "1px solid #ccc",
            borderRadius: "6px",
            padding: "8px",
          }}
        >
          <label style={{ fontWeight: "bold" }}>Acreage Filter</label>
          <div style={{ marginTop: "8px" }}>
            <label>Min Acreage:</label>
            <input
              type="number"
              value={minAcreage}
              onChange={(e) => setMinAcreage(e.target.value)}
              style={{ width: "60px", marginLeft: "10px", marginRight: "20px" }}
            />
            <label>Max Acreage:</label>
            <input
              type="number"
              value={maxAcreage}
              onChange={(e) => setMaxAcreage(e.target.value)}
              style={{ width: "60px", marginLeft: "10px" }}
            />
          </div>
          <button
            onClick={applyAcreageFilter}
            style={{
              marginTop: "10px",
              padding: "6px 12px",
              background: "#17a2b8",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Apply Filter
          </button>
          <button
            onClick={clearFilters}
            style={{
              marginTop: "10px",
              marginLeft: "10px",
              padding: "6px 12px",
              background: "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Clear Filter
          </button>
        </div>

        {/* Download Button */}
        <button
          onClick={downloadFilteredDataToExcel}
          style={{
            marginTop: "5px",
            padding: "8px 12px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            width: "100%",
          }}
        >
          Download Filtered Data (Excel)
        </button>
      </div>

      {/* The Map */}
      <MapContainer
        center={[41.2033, -77.1945]}
        zoom={7}
        style={{ height: "100%", width: "100%" }}
        ref={mapRef}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* ZIP Code Overlay */}
        <ZipOverlay />

        {/* Draw Handler for shape-based filtering */}
        <DrawHandler
          setShapeFilteredData={setShapeFilteredData}
          pricingDataRef={pricingDataRef}
          locationsRef={locationsRef}
        />

        {/* Markers from Main CSV */}
        {locations
          .filter(
            (loc) =>
              Number.isFinite(loc.coords[0]) && Number.isFinite(loc.coords[1])
          )
          .map((loc, index) => (
            <Marker key={index} position={loc.coords} icon={CustomMarkerIcon}>
              <Tooltip direction="top" offset={[0, -10]}>
                <div style={{ textAlign: "left" }}>
                  <strong>PRICE:</strong> {loc.details.PRICE}
                  <br />
                  <strong>ACRES:</strong> {loc.details.ACRES}
                </div>
              </Tooltip>
            </Marker>
          ))}

        {/* Markers from Pricing CSV (Filtered) */}
        {displayedPricingData
          .filter(
            (item) =>
              (item.coords &&
                Number.isFinite(item.coords[0]) &&
                Number.isFinite(item.coords[1])) ||
              (Number.isFinite(item.latitude) &&
                Number.isFinite(item.longitude))
          )
          .map((point, index) => (
            <Marker
              key={index}
              position={
                point.coords &&
                Number.isFinite(point.coords[0]) &&
                Number.isFinite(point.coords[1])
                  ? point.coords
                  : [point.latitude, point.longitude]
              }
              icon={DotIcon}
            >
              {point.lotAcreage !== null && (
                <Tooltip direction="top" offset={[0, -10]}>
                  <div>
                    <strong>LOT ACREAGE:</strong> {point.lotAcreage}
                  </div>
                </Tooltip>
              )}
            </Marker>
          ))}
      </MapContainer>
    </div>
  );
}

export default App;
