import React, { useState, useRef, useEffect } from "react";
import {
  MapContainer,
  TileLayer,
  Marker,
  Tooltip,
  useMap,
} from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";
import L from "leaflet";
import "leaflet-draw/dist/leaflet.draw.css";
import "leaflet-draw";
import booleanPointInPolygon from "@turf/boolean-point-in-polygon";
import { point, polygon } from "@turf/helpers";
import * as XLSX from "xlsx";

// Custom icons
import CustomMarkerIconImage from "./custom-marker.png";
import DotIconImage from "./small-dot.png";

// Main CSV icon
const CustomMarkerIcon = L.icon({
  iconUrl: CustomMarkerIconImage,
  iconSize: [30, 40],
  iconAnchor: [15, 40],
  popupAnchor: [0, -40],
});

// Pricing CSV icon
const DotIcon = L.icon({
  iconUrl: DotIconImage,
  iconSize: [10, 10],
  iconAnchor: [5, 5],
});

function ZipOverlay() {
  const map = useMap();

  useEffect(() => {
    const url =
      "https://raw.githubusercontent.com/OpenDataDE/State-zip-code-GeoJSON/master/pa_pennsylvania_zip_codes_geo.min.json";

    fetch(url)
      .then((res) => res.json())
      .then((geoData) => {
        const layer = L.geoJSON(geoData, {
          style: {
            color: "#ff7800",
            weight: 1,
            fillColor: "#ffeda0",
            fillOpacity: 0.4,
          },
          onEachFeature: (feature, layerInstance) => {
            const zip = feature.properties.ZCTA5CE10 || "Unknown ZIP";
            layerInstance.bindPopup(`ZIP Code: ${zip}`);
          },
        });
        layer.addTo(map);
        // map.fitBounds(layer.getBounds()); // Optionally auto-zoom
      })
      .catch((err) => {
        console.error("Error fetching ZIP code GeoJSON:", err);
      });
  }, [map]);

  return null;
}

function DrawHandler({
  pricingData,
  setFilteredPricingData,
  locations,
  setFilteredLocations,
}) {
  const map = useMap();
  const [controlAdded, setControlAdded] = useState(false);

  // Helper function: is point inside the drawn shape?
  const isPointInShape = (lat, lng, layer) => {
    // Circle or circleMarker => distance check
    if (layer instanceof L.Circle || layer instanceof L.CircleMarker) {
      const center = layer.getLatLng();
      const radius = layer.getRadius(); // circle radius in meters
      const distance = map.distance(center, L.latLng(lat, lng));
      return distance <= radius;
    } else {
      // Polygon or rectangle => use Turf booleanPointInPolygon
      const shapeGeoJSON = layer.toGeoJSON();
      const polygonGeometry = polygon(shapeGeoJSON.geometry.coordinates);
      const pt = point([lng, lat]);
      return booleanPointInPolygon(pt, polygonGeometry);
    }
  };

  // Whenever a shape is drawn, filter points inside it.
  const handleShapeDrawn = (layer) => {
    // Filter pricing data
    const filteredPricing = pricingData.filter((p) => {
      const [lat, lng] = p.coords;
      return isPointInShape(lat, lng, layer);
    });

    // Filter main CSV (comps) data
    const filteredComps = locations.filter((loc) => {
      const [lat, lng] = loc.coords;
      return isPointInShape(lat, lng, layer);
    });

    // Combine them into one array if desired
    const combinedData = [
      ...filteredPricing.map((item) => ({
        type: "pricing",
        apn: item.apn,
        lotAcreage: item.lotAcreage,
        latitude: item.coords[0],
        longitude: item.coords[1],
      })),
      ...filteredComps.map((item) => ({
        type: "comps",
        price: item.details.PRICE,
        acres: item.details.ACRES,
        latitude: item.coords[0],
        longitude: item.coords[1],
      })),
    ];

    setFilteredPricingData(combinedData);
    // If you want to store filtered comps separately: setFilteredLocations(filteredComps);
  };

  useEffect(() => {
    if (!controlAdded) {
      const drawnItems = new L.FeatureGroup();
      map.addLayer(drawnItems);

      const drawControl = new L.Control.Draw({
        draw: {
          marker: false,
          polyline: false,
          circle: {
            shapeOptions: {
              color: "#ff0000",
            },
            showRadius: true,
          },
          circlemarker: {
            shapeOptions: {
              color: "#ff0000",
            },
          },
          polygon: {
            shapeOptions: {
              color: "#0000ff",
            },
            showArea: true,
          },
          rectangle: {
            shapeOptions: {
              color: "#00ff00",
            },
          },
        },
        edit: {
          featureGroup: drawnItems,
        },
      });

      map.addControl(drawControl);
      setControlAdded(true);

      // When a shape is created
      map.on("draw:created", (e) => {
        const { layer } = e;
        drawnItems.addLayer(layer);
        handleShapeDrawn(layer);
      });
    }
  }, [map, pricingData, locations, controlAdded]);

  return null;
}

function App() {
  const [locations, setLocations] = useState([]); // Main CSV
  const [pricingData, setPricingData] = useState([]); // Pricing CSV
  const [filteredPricingData, setFilteredPricingData] = useState([]); // Combined filtered
  const [filteredLocations, setFilteredLocations] = useState([]); // (Optional) filtered comps

  // Acreage filter inputs
  const [minAcreage, setMinAcreage] = useState("");
  const [maxAcreage, setMaxAcreage] = useState("");

  const mapRef = useRef(null);

  // -- UPLOAD MAIN CSV (chunked) --
  const handleMainCSVUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // We’ll parse in chunks so points appear as soon as they’re ready.
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      chunkSize: 500, // adjust based on your CSV size
      chunk: function (results) {
        // Convert chunk to map points
        const chunkData = results.data
          .filter((row) => row.LATITUDE && row.LONGITUDE)
          .map((row) => ({
            coords: [
              parseFloat(row.LATITUDE),
              parseFloat(row.LONGITUDE),
            ],
            details: row,
          }));

        // Add them to existing location state
        setLocations((prev) => [...prev, ...chunkData]);
      },
      complete: function () {
        console.log("Main CSV parsing complete!");
      },
      error: function (err) {
        console.error("PapaParse error (Main CSV):", err);
      },
    });
  };

  // -- UPLOAD PRICING CSV (chunked) --
  const handlePricingCSVUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      chunkSize: 500, // chunk size for partial loading
      chunk: function (results) {
        const chunkData = results.data
          .filter(
            (row) =>
              row["LATITUDE"] && row["LONGITUDE"] && row["APN - FORMATTED"]
          )
          .map((row) => ({
            apn: row["APN - FORMATTED"],
            coords: [parseFloat(row.LATITUDE), parseFloat(row.LONGITUDE)],
            lotAcreage: row["LOT ACREAGE"]
              ? parseFloat(row["LOT ACREAGE"])
              : null,
          }));

        // Add chunk to pricingData
        setPricingData((prev) => {
          const updated = [...prev, ...chunkData];
          // Also set filteredPricingData => show everything by default
          setFilteredPricingData(updated);
          return updated;
        });
      },
      complete: function () {
        console.log("Pricing CSV parsing complete!");
      },
      error: function (err) {
        console.error("PapaParse error (Pricing CSV):", err);
      },
    });
  };

  // -- ACREAGE FILTER --
  const applyAcreageFilter = () => {
    const minVal = parseFloat(minAcreage);
    const maxVal = parseFloat(maxAcreage);

    // If either is invalid, show all
    if (isNaN(minVal) || isNaN(maxVal)) {
      setFilteredPricingData(pricingData);
      return;
    }

    // Filter pricing data by acreage
    const filtered = pricingData.filter((pd) => {
      if (pd.lotAcreage == null) return false;
      return pd.lotAcreage >= minVal && pd.lotAcreage <= maxVal;
    });
    setFilteredPricingData(filtered);
  };

  // -- DOWNLOAD & EXPORT to Excel --
  const downloadFilteredDataToExcel = () => {
    if (filteredPricingData.length === 0) {
      alert("No data points to download.");
      return;
    }

    const pricingDataTab = filteredPricingData
      .filter((item) => item.type === "pricing")
      .map((item) => ({
        APN: item.apn,
        "LOT ACREAGE": item.lotAcreage,
        Latitude: item.latitude,
        Longitude: item.longitude,
      }));

    const mainDataTab = filteredPricingData
      .filter((item) => item.type === "comps")
      .map((item) => ({
        Price: item.price,
        Acres: item.acres,
        Latitude: item.latitude,
        Longitude: item.longitude,
      }));

    const workbook = XLSX.utils.book_new();
    const pricingSheet = XLSX.utils.json_to_sheet(pricingDataTab);
    const mainSheet = XLSX.utils.json_to_sheet(mainDataTab);

    XLSX.utils.book_append_sheet(workbook, pricingSheet, "Pricing Data");
    XLSX.utils.book_append_sheet(workbook, mainSheet, "Main Data");

    XLSX.writeFile(workbook, "filtered_data.xlsx");

    // Example: remove downloaded points from the map (optional)
    const filteredCoords = new Set(
      filteredPricingData.map((item) =>
        [item.latitude, item.longitude].join(",")
      )
    );

    setLocations((prev) =>
      prev.filter((loc) => !filteredCoords.has(loc.coords.join(",")))
    );
    setPricingData((prev) =>
      prev.filter((pt) => !filteredCoords.has(pt.coords.join(",")))
    );

    // Clear the array so user sees no points after download
    setFilteredPricingData([]);
    alert("Filtered data downloaded and removed from the map.");
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      {/* Control Panel */}
      <div
        style={{
          position: "absolute",
          zIndex: 1000,
          top: 10,
          right: 10,
          background: "white",
          padding: "10px",
          borderRadius: "5px",
          boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
        }}
      >
        <div>
          <label>
            Upload Main CSV:{" "}
            <input
              type="file"
              accept=".csv"
              onChange={handleMainCSVUpload}
              style={{ display: "block", marginBottom: "10px" }}
            />
          </label>
        </div>

        <div>
          <label>
            Upload Pricing CSV:{" "}
            <input
              type="file"
              accept=".csv"
              onChange={handlePricingCSVUpload}
              style={{ display: "block", marginBottom: "10px" }}
            />
          </label>
        </div>

        <div style={{ marginTop: "10px" }}>
          <label>Min Acreage:</label>
          <input
            type="number"
            value={minAcreage}
            onChange={(e) => setMinAcreage(e.target.value)}
            style={{ width: "80px", marginLeft: "5px", marginRight: "10px" }}
          />
          <label>Max Acreage:</label>
          <input
            type="number"
            value={maxAcreage}
            onChange={(e) => setMaxAcreage(e.target.value)}
            style={{ width: "80px", marginLeft: "5px", marginRight: "10px" }}
          />
          <button onClick={applyAcreageFilter}>Apply Filter</button>
        </div>

        <button
          onClick={downloadFilteredDataToExcel}
          style={{
            marginTop: "10px",
            padding: "5px 10px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "3px",
            cursor: "pointer",
          }}
        >
          Download Filtered Data (Excel)
        </button>
      </div>

      {/* The Map */}
      <MapContainer
        center={[41.2033, -77.1945]}
        zoom={7}
        style={{ height: "100%", width: "100%" }}
        ref={mapRef}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* ZIP code overlay */}
        <ZipOverlay />

        {/* Draw tools */}
        <DrawHandler
          pricingData={pricingData}
          locations={locations}
          setFilteredPricingData={setFilteredPricingData}
          setFilteredLocations={setFilteredLocations}
        />

        {/* Markers from MAIN CSV (tooltips on hover, not permanent) */}
        {locations.map((loc, index) => (
          <Marker key={index} position={loc.coords} icon={CustomMarkerIcon}>
            <Tooltip direction="top" offset={[0, -10]}>
              <div style={{ textAlign: "left" }}>
                <strong>PRICE:</strong> {loc.details.PRICE}
                <br />
                <strong>ACRES:</strong> {loc.details.ACRES}
              </div>
            </Tooltip>
          </Marker>
        ))}

        {/* Markers from PRICING CSV (filtered by acreage or drawn shape) */}
        {filteredPricingData
          .filter((item) => item.type === undefined || item.type === "pricing")
          .map((point, index) => (
            <Marker
              key={index}
              position={[point.latitude, point.longitude]}
              icon={DotIcon}
            />
          ))}
      </MapContainer>
    </div>
  );
}

export default App;
